vec_num <- as.numeric( unlist( regmatches( txt, gregexpr( "[[:digit:]]+", txt ) ) ) )
vec_num <- vec_num[ -373 ]

vec_char <- unlist( regmatches( txt, gregexpr( "[^[:digit:]]+", txt ) ) )
vec_char <- vec_char[ -373 ]

# the odds are cities and evens are countries 
x <- c( )
for( i in 1:length( vec_char ) ) ifelse( i %% 2 == 0, x <- c( x, "country_name" ), x <- c( x, "city_name" ) )

vec_char_classified <- data.frame( x, vec_char )

# the numbers with 4 digits are city_id? the 2 digits country_id
y <- c( )
ndigits <- function( x ) nchar( trunc( abs( x ) ) )
for( i in 1:length( vec_num ) ) ifelse( ndigits( vec_num[ i ] ) == 4, y <- c( y, "city_id" ), y <- c( y, "country_id" ) )

vec_num_classified <- data.frame( y, vec_num )

df <- cbind( vec_char_classified, vec_num_classified )